import { TextInput, View, StyleSheet } from "react-native"
import PrimaryButton from "../components/PrimaryButton"
//magical! control the keyboard in the textInput
//we need to put each button in it's own view. What does this do? it will apply new flexbox containes to each button and each container will have a flex direction of column and the button will be stretched for it's column
function StartGameScreen(){
    return <View style={styles.inputContainer}>
        <TextInput style={styles.textInput} maxLength={2} keyboardType="number-pad" autoCapitalize="none"/>
        <View style={styles.buttonContainer}> 
        <View><PrimaryButton>Reset</PrimaryButton></View>
       <View><PrimaryButton>Confirm</PrimaryButton></View>
        </View>
    </View>
}

export default StartGameScreen

const styles = StyleSheet.create({
    //main view styles
    inputContainer:{
        marginHorizontal:24,
        padding: 16,
        marginTop:100,
        backgroundColor:'#72063c',
        borderRadius:8,
        //want a shadow? add elevation for Android!
        elevation:4,
        //IOS box shadow:
        shadowColor:'black', 
        shadowOffset:{width:0, height:2},
        shadowColor:6,
        shadowOpacity:0.4,
        //positions on the cross axis
        //main axis is center by default
        justifyContent:'center',
        alignItems:'center'
    },
    textInput:{
        height:50,
        width:50,
        fontSize:32,
        borderBottomColor:'#ddb52f',
        borderBottomWidth:2,
        color: '#ddb52f',
        marginVertical:8,
        fontWeight:'bold',
        textAlign:'center',
    },
    buttonContainer:{
        flexDirection:'row'
    }

})
