import {View, Text, Pressable, StyleSheet} from 'react-native'

//we want our button to be reusable in each comp, so take props on it! 
function PrimaryButton(props){
    function pressHandler(){
        console.log("clicked")
    }
    //pass an obj to the android ripple with the color for the ripple
    return (
    <View style={styles.buttonOuterContainer}>
          <Pressable style={styles.container} onPress={pressHandler} android_ripple={{color:"grey"}}>
        <Text style={styles.buttonText}>{props.children}</Text>
        </Pressable>
    </View>
 )
}

export default PrimaryButton

const styles = StyleSheet.create({
    //cont styling:
    buttonOuterContainer:{
        borderRadius:28,
        margin:4,
        // makes sure that any styling from inside, like the ripple, will be trapped in the cont
        overflow:'hidden'
    },
    container:{
        backgroundColor:"white",
        paddingVertical:8,
        paddingHorizontal:16,
        //android shadow!
        elevation:2,
    },
    buttonText:{
        color:'black',
        textAlign:'center'
    }
})
